import React, { useState, useEffect } from "react";
import axios from "axios";

const API_KEY = "YOUR_API_KEY_HERE";

const Outside = () => {
  const [airQualityData, setAirQualityData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAirQualityData = async () => {
      try {
        const response = await axios.get(
          "http://apis.data.go.kr/B552584/UlfptcaAlarmInqireSvc/getUlfptcaAlarmInfo",
          {
            params: {
              serviceKey: API_KEY,
              returnType: "json",
              numOfRows: 10,
              pageNo: 1,
              year: new Date().getFullYear(), // 현재 연도를 사용합니다.
            },
          }
        );
        setAirQualityData(response.data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchAirQualityData();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error.message}</div>;

  return (
    <div>
      <h1>오늘의 미세먼지 정보</h1>
      {airQualityData &&
        airQualityData.response.body.items.map((item, index) => (
          <div key={index}>
            <p>지역: {item.districtName}</p>
            <p>권역: {item.moveName}</p>
            <p>발령 날짜: {item.issueDate}</p>
            <p>발령 시간: {item.issueTime}</p>
            <p>발령 농도: {item.issueVal}㎍/m³</p>
            <p>경보 단계: {item.issueGbn}</p>
            <p>해제 날짜: {item.clearDate}</p>
            <p>해제 시간: {item.clearTime}</p>
            <p>해제 농도: {item.clearVal}㎍/m³</p>
            <hr />
          </div>
        ))}
    </div>
  );
};

export default Outside;
