//import styled from "styled-components";
import Outside from "./component/Outside.jsx";
import InsideDust from "./component/InsideDust.jsx";
import "./App.css";
import React from "react";
import { DUMMY_DATA } from "./data.js";
import { Unstable_Popup as BasePopup } from "@mui/base/Unstable_Popup";
import { styled } from "@mui/system";

// const Wrapper = styled.div`
//   font-style: normal;
//   background-color: ${(props) => props.boxColor};
// `;

function App() {
  const Dustset = 48;
  let selectDust = "";

  const [anchor, setAnchor] = React.useState(null);

  const handleClick = (event) => {
    setAnchor(anchor ? null : event.currentTarget);
  };

  const open = Boolean(anchor);
  const id = open ? "simple-popper" : undefined;

  if (Dustset >= 0 && Dustset <= 30) {
    selectDust = "good";
  } else if (Dustset > 30 && Dustset <= 80) {
    selectDust = "average";
  } else if (Dustset > 80 && Dustset <= 150) {
    selectDust = "bad";
  } else if (Dustset > 150) {
    selectDust = "very_bad";
  }

  return (
    <>
      <Wrapper boxColor={DUMMY_DATA[selectDust].color}>
        <InsideDust Dust={selectDust} DustRate={Dustset}></InsideDust>
        <div>
          <Button aria-describedby={id} type="button" onClick={handleClick}>
            Toggle Popup
          </Button>
          <BasePopup id={id} open={open} anchor={anchor}>
            <PopupBody>The content of the Popup.</PopupBody>
          </BasePopup>
        </div>
        <Outside></Outside>
      </Wrapper>
    </>
  );
}

export default App;


const grey = {
  50: "#F3F6F9",
  100: "#E5EAF2",
  200: "#DAE2ED",
  300: "#C7D0DD",
  400: "#B0B8C4",
  500: "#9DA8B7",
  600: "#6B7A90",
  700: "#434D5B",
  800: "#303740",
  900: "#1C2025",
};

const blue = {
  200: "#99CCFF",
  300: "#66B2FF",
  400: "#3399FF",
  500: "#007FFF",
  600: "#0072E5",
  700: "#0066CC",
};

const PopupBody = styled("div")(
  ({ theme }) => `
  width: max-content;
  padding: 12px 16px;
  margin: 8px;
  border-radius: 8px;
  border: 1px solid ${theme.palette.mode === "dark" ? grey[700] : grey[200]};
  background-color: ${theme.palette.mode === "dark" ? grey[900] : "#fff"};
  box-shadow: ${
    theme.palette.mode === "dark"
      ? `0px 4px 8px rgb(0 0 0 / 0.7)`
      : `0px 4px 8px rgb(0 0 0 / 0.1)`
  };
  font-family: 'IBM Plex Sans', sans-serif;
  font-size: 0.875rem;
  z-index: 1;
`
);

const Button = styled("button")(
  ({ theme }) => `
  font-family: 'IBM Plex Sans', sans-serif;
  font-weight: 600;
  font-size: 0.875rem;
  line-height: 1.5;
  background-color: ${blue[500]};
  padding: 8px 16px;
  border-radius: 8px;
  color: white;
  transition: all 150ms ease;
  cursor: pointer;
  border: 1px solid ${blue[500]};
  box-shadow: 0 2px 1px ${
    theme.palette.mode === "dark"
      ? "rgba(0, 0, 0, 0.5)"
      : "rgba(45, 45, 60, 0.2)"
  }, inset 0 1.5px 1px ${blue[400]}, inset 0 -2px 1px ${blue[600]};

  &:hover {
    background-color: ${blue[600]};
  }

  &:active {
    background-color: ${blue[700]};
    box-shadow: none;
  }

  &:focus-visible {
    box-shadow: 0 0 0 4px ${
      theme.palette.mode === "dark" ? blue[300] : blue[200]
    };
    outline: none;
  }

  &.disabled {
    opacity: 0.4;
    cursor: not-allowed;
    box-shadow: none;
    &:hover {
      background-color: ${blue[500]};
    }
  }
`
);
