import { DUMMY_DATA } from "../data.js";
import { useState } from "react";

export default function InsideDustState(props) {
  const [selectedDust, setSelectedDust] = useState();

  function handleSelect(selectSet) {
    if (selectSet >= 0 && selectSet <= 30) {
      setSelectedDust("good");
    } else if (selectSet > 30 && selectSet <= 80) {
      setSelectedDust("average");
    } else if (selectSet > 80 && selectSet <= 150) {
      setSelectedDust("bad");
    } else if (selectSet > 150) {
      setSelectedDust("very_bad");
    }
  }

  let tabContent = "test";

  if (selectedDust) {
    tabContent = (
      <div>
        <h3>{DUMMY_DATA[selectedDust].dust}</h3>
        <p>{DUMMY_DATA[selectedDust].description}</p>
      </div>
    );
  }
  let test = props.dusts;

  return (
    <>
      <button onClick={() => handleSelect(test)}>새로고침</button>
      <div>{tabContent}</div>
    </>
  );
}
