import styled from "styled-components";
import Outside from "./component/Outside.jsx";
import InsideDust from "./component/InsideDust.jsx";
import "./App.css";
import { DUMMY_DATA } from "./data.js";
import CustomPopup from "./component/Popup.jsx";
import { useState, useEffect } from "react";
import Button from "./component/Button.jsx";
import { uid } from "uid";
import { fb } from "./firebase.js";
import { ref, set } from "firebase/database";

const Wrapper = styled.div`
  font-style: normal;
  background: linear-gradient(white, ${(props) => props.boxColor});
  padding: 0;
`;

function App() {
  const [visibility, setVisibility] = useState(false);

  const popupCloseHandler = (e) => {
    setVisibility(e);
  };

  const [ipAddress, setIpAddress] = useState("");

  useEffect(() => {
    const airQualityRef = fb
      .database()
      .ref("/air_quality/air_quality/ip_address");
    airQualityRef.on("value", (snapshot) => {
      const ip = snapshot.val();
      setIpAddress(ip);
    });

    return () => airQualityRef.off();
  }, []);

   const updateIpAddress = () => {
     const newIp = "123.456.789.000"; // 예시 IP 주소
     firebfase.database().ref("/air_quality/air_quality/ip_address").set(newIp);
   };

  const Dustset = 18;
  let selectDust = "";
  if (Dustset >= 0 && Dustset <= 30) {
    selectDust = "good";
  } else if (Dustset > 30 && Dustset <= 80) {
    selectDust = "average";
  } else if (Dustset > 80 && Dustset <= 150) {
    selectDust = "bad";
  } else if (Dustset > 150) {
    selectDust = "very_bad";
  }

  return (
    <>
      <Wrapper boxColor={DUMMY_DATA[selectDust].color}>
        <InsideDust Dust={selectDust} DustRate={Dustset}></InsideDust>
        <h1>Air Quality Sensor IP Address</h1>
        <p>{ipAddress}</p>
        <div className="App">
          <Button onClick={(e) => setVisibility(!visibility)}>
            야외 미세먼지는?
          </Button>
          <CustomPopup
            onClose={popupCloseHandler}
            show={visibility}
            title=" 야외 미세먼지는?"
          >
            <Outside></Outside>
          </CustomPopup>
        </div>
      </Wrapper>
    </>
  );
}

export default App;
