import styled from "styled-components";
import Outside from "./component/Outside.jsx";
import InsideDust from "./component/InsideDust.jsx";
import "./App.css";
import { DUMMY_DATA } from "./data.js";
import CustomPopup from "./component/Popup.jsx";
import { useState } from "react";
import Button from "./component/Button.jsx";
import { db } from "./firebase.js";
import { doc, getDoc } from "firebase/firestore";

const Wrapper = styled.div`
  font-style: normal;
  background: linear-gradient(white, ${(props) => props.boxColor});
  padding: 0;
`;

function App() {
  const [test, setTest] = useState();
  // async - await로 데이터 fetch 대기
  async function getTest() {
    // document에 대한 참조 생성
    const docRef = doc(db, "items", "1");
    // 참조에 대한 Snapshot 쿼리
    const docSnap = await getDoc(docRef);

    if (docSnap.exists()) {
      setTest(docSnap.data());
    }
  }
  // 최초 마운트 시에 getTest import
  useEffect(() => {
    getTest();
  }, []);

  const [visibility, setVisibility] = useState(false);

  const popupCloseHandler = (e) => {
    setVisibility(e);
  };

  const Dustset = 88;
  let selectDust = "";
  if (Dustset >= 0 && Dustset <= 30) {
    selectDust = "good";
  } else if (Dustset > 30 && Dustset <= 80) {
    selectDust = "average";
  } else if (Dustset > 80 && Dustset <= 150) {
    selectDust = "bad";
  } else if (Dustset > 150) {
    selectDust = "very_bad";
  }

  return (
    <>
      <Wrapper boxColor={DUMMY_DATA[selectDust].color}>
        <InsideDust Dust={selectDust} DustRate={Dustset}></InsideDust>
        <div>{test !== undefined && <div>{test.name}</div>}</div>
        <div className="App">
          <Button onClick={(e) => setVisibility(!visibility)}>
            야외 미세먼지는?
          </Button>
          <CustomPopup
            onClose={popupCloseHandler}
            show={visibility}
            title=" 야외 미세먼지는?"
          >
            <Outside></Outside>
          </CustomPopup>
        </div>
      </Wrapper>
    </>
  );
}

export default App;
